const audioFiles = [
	"/audio/ScreamingPiha1 - Copy.wav",
	"/audio/ScreamingPiha2 - Copy.wav",
	"/audio/ScreamingPiha4 - Copy (6).wav",
	"/audio/ScreamingPiha5 - Copy (3).wav",
	"/audio/ScreamingPiha6 - Copy (3).wav",
	"/audio/ScreamingPiha7 - Copy (3).wav",
	"/audio/ScreamingPiha7 - Copy.wav",
	"/audio/ScreamingPiha9 - Copy (3).wav",
	"/audio/ScreamingPiha10 - Copy.wav",
	"/audio/ScreamingPiha11.wav",
]

const spectrograms = []
const Spectrogram = ({id, filePath, onMouseEnter, onMouseLeave}) => {
	const wavesurferRef = useRef(null);
	const containerRef = useRef(null);
	const innerRef = useRef(null);
	const [isRed, setIsRed] = useState(false);
	const [isLoaded, setIsLoaded] = useState(false);

	const toggleRed = () => {
		setIsRed(prev => !prev);
	};

	useEffect(() => {
		if (!wavesurferRef.current) {;
			wavesurferRef.current = WaveSurfer.create({
				container: innerRef.current,//`#waveform-${id}`,
				height: 0,
				fillParent: true,
			});
			wavesurferRef.current.registerPlugin(
				SpectrogramPlugin.create({
					colorMap: createViridisColorMap(),
					height: 128,
					fftSamples: 256,
				}),
			)

			wavesurferRef.current.load(filePath);
			spectrograms[id] = {
				ws: wavesurferRef.current,
				container: innerRef.current,
				isRed: isRed,
				toggleRed: toggleRed,
			};
			wavesurferRef.current.on('ready', function() {
				document.getElementById(`loading-spinner-${id}`).style.display = 'none';
				setIsLoaded(true);
				console.log(id);
			});
		}

		return () => { wavesurferRef.current.destroy() };
	}, []);

	// left click reposition functionality already handled by wavesurfer
	const rightClickPlayPause = (e: MouseEvent) => { // play/pause on right click
		e.preventDefault();
		wavesurferRef.current.playPause();
	}

	return (
		<div 
			key={id} 
			className={`${styles.waveContainer} ${isLoaded && (isRed ? styles.redOutline : styles.greenOutline)}`}
			ref={containerRef}
			onMouseEnter={onMouseEnter}
			onMouseLeave={onMouseLeave}
		>
			<div id={`loading-spinner-${id}`} className={styles.waveLoadingCircle}></div>
			<div 
				id={`waveform-${id}`} 
				ref={innerRef}
				style={{ width: "100%", height: "128px"}} 
				onContextMenu={rightClickPlayPause}
			></div>
		</div>	
	)
}

export default function VerifyPage() {
	const [activeSpectrogram, setActiveSpectrogram] = useState(null);
	useEffect(() => {
        const handleKeyDown = (event) => {
            if (event.code === "Space" && activeSpectrogram !== null) {
				event.preventDefault(); 
				setActiveSpectrogram(activeSpectrogram);
				spectrograms[activeSpectrogram].toggleRed();
			}
        };

        window.addEventListener("keydown", handleKeyDown);
        return () => window.removeEventListener("keydown", handleKeyDown);
    }, [activeSpectrogram]);

	
	return (
		<React.Fragment>
			<Head>
				<title>Verify Page</title>
			</Head>
			<div className ={styles.container}>
				<SideBar/>

				<div className={styles.grid}>
					{audioFiles.map((filePath, i) => (
						<Spectrogram 
							key={i}
							id={i} 
							filePath={filePath} 
							onMouseEnter={() => setActiveSpectrogram(i)}
							onMouseLeave={() => setActiveSpectrogram(null)}
						/>
					))}
				</div>
			</div>
		</React.Fragment>
	)
}